/*
 * Custom
 */
$(document).ready(function(){
	// slider
		 
	$('.block-advantages-list-slider').slick({
	  dots: true,
	  arrows:false,
	  infinite: true, 
	  speed: 300,   
	  slidesToShow: 3,
	  slidesToScroll: 1, 
	  responsive: [
	    {
	      breakpoint: 768,
	      settings: {
	        slidesToShow: 1,
	        slidesToScroll: 1,
	        infinite: true,
	        arrows:false,
	        dots: true,
	        centerMode: true,
	        centerPadding: '40px'
	      }
	    },
	    {
	      breakpoint: 560,
	      settings: {
	        slidesToShow: 1,
	        slidesToScroll: 1,
	        infinite: true,
	        arrows:false,
	        dots: true, 
	        centerMode: true,
	        centerPadding: '40px'
	      }
	    }    
	    ]
	}); 
	
	 
	
	$('.about-robots-slider').slick({
	  dots: false,
	  arrows:true,  
	  slidesToShow: 1,
	  slidesToScroll: 1
	});
	  
	
	/*classic-strategies-block-item-info--bottom text*/
	$('.description').on('click', function(e){
		$(this).toggleClass('open');
		$(this).next().slideToggle();
		$(this).closest('.classic-strategies-block-item').toggleClass('open');
	});
	
	/*questions open*/
	$('.questions-block-item').on('click', function(e){
		$(this).toggleClass('open');
		$(this).find('.questions-block-text').slideToggle(); 
	});
	
	
	
	
	/*animate*/
	
	$('.post').addClass("hidden").viewportChecker({
		classToAdd: 'visible animated bounceInUp',
		offset: 200
	});
	
	$('.left  ').addClass("hidden").viewportChecker({
		classToAdd: 'visible animated bounceInLeft',
		offset: 300
	});
	
	 
	
	$('.right  ').addClass("hidden").viewportChecker({
		classToAdd: 'visible animated bounceInRight',
		offset: 100
	});
	
	$('.post2').addClass("hidden").viewportChecker({
		classToAdd: 'visible animated fadeInDown',
		offset: 100
	});
	
	$('.fadein').addClass("hidden").viewportChecker({
		classToAdd: 'visible animated fadeIn',
		offset: 100
	});
	
	 
	
	/*popup*/ 
	$('.js-btn-vpn').on('click', function(){
		$('.overlay').addClass('overlay-visible'); 
		$('.popup').addClass('visible'); 
	});	
	$('.popup-close').on('click', function(){
		$('.overlay').removeClass('overlay-visible'); 
		$('.popup').removeClass('visible'); 
	});	
	
	
	
	/*language*/
		$('.language-active').on('click', function(){
			
			$(this).next().addClass('active');
		});
	
		$('.language li').on('click', function(){
			var lang = $(this).text(); 
			$(this).closest('.language').find('.language-active').text(lang);
			$(this).parent().removeClass('active'); 
		});	
	
	
	/*nav*/
		$('.nav__mob-btn').on('click', function(){
			$('.nav').toggleClass('nav-open');
		});	
	
	/*jsDropdown*/
	var jsDropdown = $('.nav-item');
	
	jsDropdown.each(function() {			
		$(this).has('.sub-nav-list').addClass('js-dropdown').prepend("<span class='dropdown-icn'> </span>"); 
	}); 
	
	$('.nav-item.js-dropdown').on('click', function(){ 
	    $(this).toggleClass('open');
	    $(this).find('.sub-nav-list').slideToggle(); 
	});
	
	/*end jsDropdown*/
	
	
	
	/*form-select-time*/
	$(".fl_inp").change(function(){
		var filename = $(this).val().replace(/.*\\/, "");
		$(".fl_nm").html(filename); 
	});
	
	$('.select-time').on('click', function(){
		$(this).closest('.form-select-time').addClass('open');
	});
	/*form-select-time*/
	
	/*requests*/
	$('.js-requests-block-more').on('click', function(e){ 
		$(this).toggleClass('open');
		$(this).next('.requests-block-content-more').slideToggle(); 
		var text = $(this).find("strong").text();
		if (text == 'Подробнее'){
		    $(this).find("strong").text('Свернуть');
		}else{
			$(this).find("strong").text('Подробнее');
		}
	});
	/*requests*/
});
function widthScreen() {
  var widthSlideItem = $(document).width();
 

  if(widthSlideItem <= 768) {
    $('.block-advantages-list-slider--active').addClass('block-advantages-list-slider'); 
  }else{
    $('.block-advantages-list-slider--active').removeClass('block-advantages-list-slider'); 
  }  
}


widthScreen();

$(window).resize(function() {
    widthScreen();
});





 
$(window).load(function() {
	$('.globload').delay(7500).fadeOut("slow");
	
	$('.snaker').delay(2000).fadeOut("slow");  

	setTimeout(function() {
		$('h1.tlt').textillate({ in: { effect: 'fadeInUp', shuffle: false, sync: false, delayScale: 2.0  }});
	}, 2100); 

	setTimeout(function() { 
		$(".tlt").addClass("fadeIn animated");
	}, 2050);	

	setTimeout(function() { 
		$(".tlt").addClass("fadeOut animated");
	}, 6000);
	
	setTimeout(function() { 
		$(".tlt").addClass("display-none");
	}, 7500);	 
});